import React, { useState } from "react";
import logo from '../logo.png';
import './signup.css';

const Signup = () =>{

    const [user,setUser] = useState({
        name:"",
        email:"",
        password:"",
        repassword:""
    })
    const handleChange = e =>{
        const { name, value } = e.target
        console.log(name,value)
    }

    return (
        <>
        <img src={logo} alt="logo"  />
        <div className="signup">
        <h1>Sign Up</h1>
        <div> <input type="text" name="name" value={user.name} placeholder="Name" onChange={handleChange} /></div>
        <div> <input type="text" name="email" value={user.email} placeholder="E-mail Or Phone Number" onChange={handleChange} /></div>
        <div> <input type="password" name="password" value={user.password} placeholder="Password" onChange={handleChange} /></div>
        <div> <input type="password" name="repassword" value={user.repassword} placeholder="Re-type Password" onChange={handleChange} /></div>
        <div><button>Sign Up</button></div>

        <div><a href="/"> Log In ?</a></div>
        </div>
        </>
    );
}
export default Signup;
