import React, {useState} from "react"
import logo from '../logo.png';
import './login.css';

const Login = () =>{

    const [user,setUser] = useState({
        email:"",
        password:""
    })
    const handleChange = e =>{
        const {name,value} = e.target
        setUser({
           ...user,
           [name]:value
        })
    }

    return (
        <>
        <img src={logo} alt="logo"  />
        <div className="login">
        <h1>Log In</h1>
        <div> <input type="text" name="email" value={user.name} placeholder="E-mail Or Phone Number" onChange={handleChange}/></div>
        <div> <input type="text" name="password" value={user.password} placeholder="Password" onChange={handleChange}/></div>
        <div><button>Log In</button></div>

        <div><a href="/"> Sign Up ?</a></div>
        <a href="/">Reset Password ?</a>
        </div>
        </>
    );
}
export default Login;
